import React from 'react';
import { Card, ListGroup } from 'react-bootstrap';

const CardComponent = ({ logo, nombre, habilidades, empresa, lugar, salario, vacantes, actualizado, beneficios }) => {
 return (
<Card style={{ width: '100%', marginBottom: '1rem', borderRadius: '10px', boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2)' }} className="w-100 h-100">
 <Card.Body style={{ display: 'flex', justifyContent: 'space-between' }}>
    <div style={{ alignSelf: 'center' }}>
      <img src={logo} alt="Logo de la empresa" style={{ width: '70px', height: '70px', borderRadius: '5px' }} />
    </div>
    <div style={{ flex: 1, display: 'flex', flexDirection: 'column', flex: '0 0 83.333333%', maxWidth: '83.333333%' }}>
      <div style={{ display: 'flex', alignItems: 'center'}}>
        <div class="mb-2 col">
          <Card.Title style={{ fontWeight: 'bold', fontSize: '1.25rem' }}>{nombre}</Card.Title>
          <ListGroup variant="flush" style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
              {Array.isArray(habilidades) && habilidades.map((habilidad, index) => (
                  <ListGroup.Item key={index} style={{ marginRight: '5px' }}>{habilidad}</ListGroup.Item>
              ))}
          </ListGroup>
        </div>
      </div>
      <div style={{ display: 'flex' }}>
        <Card.Subtitle className="mb-2 text-muted">{empresa}</Card.Subtitle>
        <p>{lugar}</p>
        <p>Salario: {salario}</p>
        <p>Vacantes: {vacantes}</p>
        <p>Actualizado: {actualizado}</p>
        <p>Beneficios: {beneficios}</p>
      </div>
    </div>
 </Card.Body>
</Card>
 );
};

export default CardComponent;
