import React from 'react';
import { Card, Container, Row, Col, ListGroup } from 'react-bootstrap';

const CardComponent = ({ logo, nombre, habilidades, empresa, lugar, salario, vacantes, actualizado, beneficios }) => {
  return (
    <Card style={{ width: '100%', marginBottom: '1rem', borderRadius: '10px', boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2)' }} className="w-100 h-100">
       <Card.Body>
         <Row>
           <Col xs={4}>
             <img src={logo} alt="Logo de la empresa" style={{ width: '70px', height: '70px', borderRadius: '5px' }} />
           </Col>
           <Col xs={8}>
             <Row>
               <Col xs={12}>
                 <Card.Title style={{ fontWeight: 'bold', fontSize: '1.25rem' }}>{nombre}</Card.Title>
                 <ListGroup variant="flush">
                   {Array.isArray(habilidades) && habilidades.map((habilidad, index) => (
                    <ListGroup.Item key={index}>{habilidad}</ListGroup.Item>
                   ))}
                 </ListGroup>
               </Col>
             </Row>
           </Col>
         </Row>
         <Row>
           <Col xs={12} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
             <Card.Subtitle className="mb-2 text-muted">{empresa}</Card.Subtitle>
             <Card.Text>
               {lugar} <br />
               Salario: {salario} <br />
               Vacantes: {vacantes} <br />
               Actualizado: {actualizado} <br />
               Beneficios: {beneficios}
             </Card.Text>
           </Col>
         </Row>
       </Card.Body>
    </Card>
   );   
};

export default CardComponent;
